if __name__ == '__main__':
    # test that the partitions don't overlap
    num_classes = {
        'mini-imagenet': 100,
        'tiered-imagenet': 608,
        'lfw10': 158,
        'emnist': 62,
        'cifar100': 100,
    }
    for name in ['mini-imagenet', 'tiered-imagenet', 'lfw10', 'cifar100', 'emnist']:
        for taskset in ['easy', 'medium-easy', 'medium-hard', 'hard']:
            train_classes = set(_ALL_SPLITS[name][taskset]['train_classes'])
            valid_classes = set(_ALL_SPLITS[name][taskset]['valid_classes'])
            test_classes = set(_ALL_SPLITS[name][taskset]['test_classes'])
            n_classes_taskset = len(train_classes) + len(valid_classes) + len(test_classes)
            assert num_classes[name] == n_classes_taskset, f'num classes mismatch: {name} - {taskset}'
            empty_set = set()
            assert set.intersection(train_classes, valid_classes) == empty_set
            assert set.intersection(train_classes, test_classes) == empty_set
            assert set.intersection(test_classes, valid_classes) == empty_set
            # print(f'num classes in {name}:', n_classes_taskset)
